import styled from 'styled-components';

const categories = [
{
    name: 'announcement',
    text: '공지사항'
    },
    {
        name:'seminar',
        text: '세미나'
    },
    {
        name: 'project',
        text: '프로젝트'
},
    {
        name: 'study',
        text: '스터디'
    },
    {
        name: 'networking',
        text: '친목'
    },
    {
        name: 'introduce',
        text: '소개'
    }
    ];

    const CategoriesBlock = styled.div`
    display: flex;
    padding: 1rem;
    width: 768px;
    margin: 0 auto;
    @media screen and (max-width: 768px) {
        width: 100%;
        overflow-x: auto;
    }
    `;

   const Categories = styled.div`
    font-size: 1.125rem;
    cursor: pointer;
    white-space: pre;
    text-decoration: none;
    color: inherit;
    padding-bottom: 0.25rem;

    &:hover {
    color: #495057;
    }

    &+& {
    margin-left: 1rem;
    }
   `;

   const Category = () => {
   return (
   <CategoriesBlock>
   {categories.map(c => (
   <Category key = {c.name}>{c.text}</Category>
   ))}
   </CategoriesBlock>
   );
   };
   export default Categories;
