import React, {useEffect} from 'react';
import styled from 'styled-components';
import { Link, withRouter } from 'react-router-dom';
import LogoutButton from './LogoutButton';
import LogoutBox from './LogoutBox';
import { useSelector } from 'react-redux';


const MyPageFormBlock = styled.table`
    width: 50%;
    height: 400px;
    border-radius: 10px;
    background-color: #ffffff;
    box-shadow: 0px 0px 2px 1px rgba(0, 0, 0, 0.1);
`;

const LineBlock = styled.tr`
    width: 100%;
    padding: 0;
    margin: auto 0;
`;

const FullLine = styled.td`
    display: flex;
    justify-content: center;
    align-items: center;
    width: 100%;
    height: 100px;
    border-bottom: 2px solid #dcdcdc;
    padding: 0;
    margin: 0;
`;

const HalfLeftLine = styled.td`
    display: flex;
    justify-content: center;
    align-items: center;
    float: left;
    width: 50%;
    height: 100px;
    border-bottom: 2px solid #dcdcdc;
    border-right: 2px solid #dcdcdc;
    padding: 0;
    margin: 0;
`;

const HalfRightLine = styled.td`
    display: flex;
    justify-content: center;
    align-items: center;
    float: left;
    width: 49.5%;
    height: 100px;
    border-bottom: 2px solid #dcdcdc;
    padding: 0;
    margin: 0;
`;

const TextBox = styled.div`
    margin-left: 20px;
    font-size: 20px;
`;



const MyPageForm = ({ history }) => {
    const { user } = useSelector(({ user }) => ({
        user: user.user
    }));

    //useEffect(() => {
      //  if(!user) {
        //   history.push('usern');
        //}
    //}, [history, user]);

    return(
        <>
     { user &&
        <MyPageFormBlock>
            <LineBlock>
                <FullLine>
                    <TextBox>
                        안녕하세요 <b>nickname</b> 님!
                    </TextBox>
                    <LogoutBox>
                        <LogoutButton />
                    </LogoutBox>
                </FullLine>
            </LineBlock>
            <LineBlock>
                <HalfLeftLine>
                    <TextBox>
                        <Link to="#">
                            회원 정보 수정
                        </Link>
                    </TextBox>
                </HalfLeftLine>
                <HalfRightLine>
                    <TextBox>
                        <Link to="#">
                            내 게시글
                        </Link>
                    </TextBox>
                </HalfRightLine>
            </LineBlock>
            <LineBlock>
                <HalfLeftLine>
                    <TextBox>
                        <Link to="#">
                            찜 리스트
                        </Link>
                    </TextBox>
                </HalfLeftLine>
                <HalfRightLine>
                    <TextBox>
                        <Link to="#">
                            쪽지함
                        </Link>
                    </TextBox>
                </HalfRightLine>
            </LineBlock>
            <LineBlock>
                <FullLine>
                    <TextBox>
                        <Link to="#">
                            대여 내역
                        </Link>
                    </TextBox>
                </FullLine>
            </LineBlock>
        </MyPageFormBlock>
        }
         </>
        );
    };
export default withRouter(MyPageForm);
